import type { _ } from "hkts/src";
import type { IdObject } from "../../resources";
import type { Process } from "../../process";
import * as Base from "../batch";
export type Config = {
    assignment: {};
    properties: {};
    entry?: any;
    result?: any;
};
export type Assignment<C extends Config> = {
    resource: IdObject;
} & C["assignment"];
export type Properties<C extends Config> = C["properties"];
export type Structure<_C extends Config> = {
    project: IdObject<"projects">;
    collectionName: string;
    assignments: _[];
};
export type Breadcrumb<_C extends Config> = {
    assignmentIndex: number;
};
export type Input<C extends Config> = Assignment<C>;
export type Output<C extends Config> = Input<C> & Properties<C>;
export type Entry<C extends Config> = C["entry"];
export type Result<C extends Config> = C["result"];
export type Batch<C extends Config> = {
    structure: Structure<C>;
    breadcrumb: Breadcrumb<C>;
    input: Input<C>;
    output: Output<C>;
    entry: Entry<C>;
    result: Result<C>;
};
export type Options<C extends Config> = Omit<Base.Options<Batch<C>>, "iterate" | "find" | "initialize" | "merge">;
export declare const configure: <C extends Config>(options: Options<C>) => <I extends Input<C>, O extends Output<C>>(options: {
    project: IdObject<"projects">;
    assignments: {
        [collectionName: string]: I[];
    };
}) => Generator<import("../../meta/process/types").GraphQlRequest | import("../../meta/process/types").Web3Request, {
    project: IdObject<"projects">;
    assignments: {
        [collectionName: string]: (I & O)[];
    };
}, any>;
