class Recipe {
  static help = "Mock Recipe";

  name = "truffle-mock";
  inputLabels = [];
  outputLabels = [];

  constructor(environment) {
    this.environmentName = environment.selectedEnvironment;
  }

  async *execute({ controls }) {
    yield* controls.update({
      message: `Provided environment name: ${this.environmentName}`
    });
    return "Successfully called truffle-mock:preserve()";
  }
}

module.exports = { Recipe };
